package com.Chapter3.com;

public class Calculation02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 46;
		int j = 97;
		char c = 'a';
		System.out.println("46&97的值：" + (i&j));
		System.out.println("46^a的值：" + (i^c));
		System.out.println("46>>1的值：" + (i>>1));
	}

}
