package com.Chapter3.com;

public class Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte mybyte = 126;
		short myshort = 15865;
		int myint = 415578948;
		long mylong = 4515895541L;
		long result = mybyte + myshort + myint + mylong;
		System.out.println("四种类型相加的结果为：" + result);
	}

}
